mapper.update:
UPDATE cu_professor SET
address = #{address}, tel =
#{tel}, email = #{email}
WHERE id = #{id}

mapper.select:
SELECT
count(id) FROM cu_professor
WHERE dept_id = #{deptId}

SELECT id
FROM cu_professor
WHERE name = #{name}
AND birth_date = #{birthDate}
AND
gender = #{gender}
AND address =#{address}
AND tel = #{tel}
AND dept_id =
#{deptId}
AND email = #{email}

SELECT id
FROM
cu_professor
WHERE name = #{name}
AND email = #{email}

SELECT id
FROM cu_professor
WHERE id = #{id}
AND name = #{name}
AND email = #{email}
