mapper.select:
SELECT
count(id) FROM cu_student
WHERE dept_id = #{deptId}

SELECT id
FROM cu_student
WHERE name = #{name}
AND birth_date = #{birthDate}
AND
gender = #{gender}
AND address =#{address}
AND tel = #{tel}
AND
dept_id =
#{deptId}
AND entrance_date = #{entranceDate}
AND email = #{email}

SELECT id
FROM
cu_student
WHERE name = #{name}
AND email = #{email}

SELECT id
FROM cu_student
WHERE id = #{id}
AND name = #{name}
AND email = #{email}

SELECT address, tel, email
FROM cu_student
WHERE id = #{userId}

mapper.update:
UPDATE cu_student
SET address = #{address},
tel = #{tel},
email = #{email}
WHERE id = #{userId}

mapper.insert:
INSERT INTO cu_break
	(student_id, student_grade, from_year, from_semester, to_year, to_semester, type, app_date)
VALUES (#{userId}, #{studentGrade}, #{fromYear},#{fromSemester}, #{toYear},#{toSemester},#{type},#{appDate});
